mapper.select:
  SELECT   	

		top  ${itemsCount} 

  	Id, Amount,RecordType,OperateTime,Remark
    from ClienterBalanceRecord with(nolock)
    where ClienterId = #{userId,jdbcType=BIGINT}

        and Amount>=0


        and 0>Amount


	     and  ${nextId} > id 

	order by id desc;

mapper.insert:
       insert into
		ClienterBalanceRecord
		(ClienterId,Amount,AfterAmount,RecordType,OptName,OrderId,RelationNo,Remark,status)
		select #{clienterId,jdbcType=INTEGER},
		#{amount,jdbcType=DECIMAL},cb.Balance+#{amount,jdbcType=DECIMAL},#{recordType,jdbcType=SMALLINT},#{optName,jdbcType=NVARCHAR}
		,#{orderId,jdbcType=BIGINT},#{relationNo,jdbcType=VARCHAR},#{remark,jdbcType=NVARCHAR},#{status,jdbcType=SMALLINT}
		from dbo.ClienterBalance  as cb where cb.ClienterId=#{clienterId,jdbcType=BIGINT} ;
		select @@IDENTITY

mapper.update:
update ClienterBalanceRecord
set   status = #{status,jdbcType=SMALLINT}   
where OrderId = #{orderId,jdbcType=BIGINT}

mapper.sql:
cbr.ClienterId=${clienterId}

	AND cbr.recordType=${recordType}


	AND cbr.orderId=${orderId}


	AND cbr.OperateTime > ''${beginDate}''


	AND  ''${endDate}''> cbr.OperateTime
