' cwf.Id  ',
'

',
'ClienterWithdrawForm cwf WITH (NOLOCK) left join Clienter c with (nolock) on cwf.ClienterId=c.Id',
'

',
#{pageSize,mode=IN,jdbcType=INTEGER},
#{currentPage,mode=IN,jdbcType=INTEGER}
,1,
#{totalRecord,mode=OUT,jdbcType=INTEGER},
#{totalPage,mode=OUT,jdbcType=INTEGER}
)}

mapper.update:
    UPDATE  dbo.AlipayBatch 
    	SET LastOptUser=#{lastOptUser,jdbcType=VARCHAR},
        	LastOptTime = GETDATE(),
        	Remarks=Remarks + #{remarks,jdbcType=VARCHAR}+'',
        	BatchNo=#{newBatchNo,jdbcType=VARCHAR}
		WHERE BatchNo = #{batchNo,jdbcType=VARCHAR}
