mapper.select:
select  * 
from    dbo.PublicProvinceCity a ( nolock )
where ispublic=1  AND a.Jibie=2 OR Jibie=3

update.if:
	     update dbo.PublicProvinceCity set IsPublic = 1 where Code in 

	           ${item}
	      ;
	     update dbo.PublicProvinceCity set IsPublic = 1 where Code in (
	       SELECT  ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	      );
	     update dbo.PublicProvinceCity set IsPublic = 1 where Code in (
	      SELECT c.ParentCode  FROM  PublicProvinceCity d WITH(NOLOCK) 
	      JOIN PublicProvinceCity c ON c.code=d.ParentCode
                                        WHERE d.code in

	           ${item}
	     );

	     update dbo.PublicProvinceCity set IsHot = 1 where Code in 

	           ${item}
	      ;
	     update dbo.PublicProvinceCity set IsHot = 1 where Code in (
	     SELECT  ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	      );
	     update dbo.PublicProvinceCity set IsHot = 1 where Code in (
	     SELECT c.ParentCode  FROM  PublicProvinceCity d WITH(NOLOCK) 
	      JOIN PublicProvinceCity c ON c.code=d.ParentCode
                                        WHERE d.code in

	           ${item}
	     );

update dbo.PublicProvinceCity set IsPublic = 0 where Code in 

      ${item}
 ;
update dbo.PublicProvinceCity set IsPublic = 0 where Code in (
 SELECT  ParentCode  FROM PublicProvinceCity WHERE code in 

      ${item}
 );

update dbo.PublicProvinceCity set IsHot = 0 where Code in 

      ${item}
 ;
update dbo.PublicProvinceCity set IsHot = 0 where Code in (
  SELECT  ParentCode  FROM PublicProvinceCity WHERE code in 

      ${item}
 );
