mapper.select:
select  * 
from    dbo.PublicProvinceCity a ( nolock )
where a.ispublic=1  AND (a.Jibie=2 OR a.Jibie=3)

update.if:
  	 	 DECLARE @cityCode INT

	     update dbo.PublicProvinceCity set IsPublic = 1 where Code in 

	           ${item}
	      ;
	     SELECT  @cityCode=ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	     ;

	     update dbo.PublicProvinceCity set IsPublic = 1 where Code =@cityCode;

	     update dbo.PublicProvinceCity set IsPublic = 1 where Code in (
	      SELECT d.ParentCode  FROM  PublicProvinceCity d WITH(NOLOCK) where d.code=@cityCode
	      );

     	 DECLARE @closePublicCityCode INT
     	 DECLARE @closePublicProvenceCode INT
	     update dbo.PublicProvinceCity set IsPublic = 0 where Code in 

	           ${item}
	      ;
	      SELECT  @closePublicCityCode=ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	     ;
	     IF not EXISTS(select 1 from PublicProvinceCity where IsPublic=1 and ParentCode=@closePublicCityCode)
	     update dbo.PublicProvinceCity set IsPublic = 0 where Code =@closePublicCityCode;

	     SELECT @closePublicProvenceCode=ParentCode  FROM  PublicProvinceCity  WITH(NOLOCK) where code=@closePublicCityCode;
	  	IF not EXISTS(select 1 from PublicProvinceCity where IsPublic=1 and ParentCode=@closePublicProvenceCode)
	    update dbo.PublicProvinceCity set IsPublic = 0 where Code =@closePublicProvenceCode;

     	 DECLARE @hotCityCode INT
	     update dbo.PublicProvinceCity set IsHot = 1 where Code in 

	           ${item}
	      ;
	     SELECT  @hotCityCode=ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	     ;
	     update dbo.PublicProvinceCity set IsHot = 1 where Code =@hotCityCode;

	     update dbo.PublicProvinceCity set IsHot = 1 where Code in (
	     SELECT d.ParentCode  FROM  PublicProvinceCity d WITH(NOLOCK) where d.code=@hotCityCode
	     );

             DECLARE @closeHotCityCode INT
           	 DECLARE @closeHotProvenceCode INT
	     update dbo.PublicProvinceCity set IsHot = 0 where Code in 

	           ${item}
	      ;
	      SELECT  @closeHotCityCode=ParentCode  FROM PublicProvinceCity WHERE code in 

	           ${item}
	     ;
	     IF not EXISTS(select 1 from PublicProvinceCity where IsHot=1 and ParentCode=@closeHotCityCode)
	     update dbo.PublicProvinceCity set IsHot = 0 where Code =@closeHotCityCode;

	    SELECT @closeHotProvenceCode=ParentCode  FROM  PublicProvinceCity  WITH(NOLOCK) where code=@closeHotCityCode;
	  	IF not EXISTS(select 1 from PublicProvinceCity where IsHot=1 and ParentCode=@closeHotProvenceCode)
	    update dbo.PublicProvinceCity set IsHot = 0 where Code =@closeHotProvenceCode;
