mapper.select:
{call Sp_CustomPage2015_V1(
' a.Id DESC ',
'a.*,b.templateName,c.CompanyName as businessName',
'RenRenTask a  WITH (NOLOCK) join TemplateSnapshot b  WITH (NOLOCK) on a.snapshotTemplateId=b.id 
join Business c  WITH (NOLOCK) on a.businessId=c.id',
'

',
#{pageSize,mode=IN,jdbcType=INTEGER},
#{currentPage,mode=IN,jdbcType=INTEGER}
,1,
#{totalRecord,mode=OUT,jdbcType=INTEGER},
#{totalPage,mode=OUT,jdbcType=INTEGER}
)}

select
top ${itemsCount}
rt.Id TaskId , rt.TaskGeneralInfo , rt.Pusher , rt.TaskTitle TaskName , rt.Amount , rt.AvailableCount , case when o.OrderStatus is null then -1 else o.OrderStatus end [Status], o.AuditStatus , rt.BeginTime , rt.EndTime , rt.PaymentMethod, b.logo , o.Id orderId from dbo.RenRenTask rt ( nolock ) left join dbo.[Order] o(nolock) on rt.Id = o.TaskId and o.ClienterId = ${userId} and o.OrderStatus=0 join dbo.Business b (nolock) on rt.BusinessId = b.Id where o.ClienterId is null and rt.Status = 1 and getdate()>=rt.BeginTime and rt.EndTime>getdate() and rt.AvailableCount>0
and rt.Id > ${nextId}
order by rt.Id asc;

select
top ${itemsCount}
rt.Id TaskId , rt.TaskGeneralInfo , rt.Pusher , rt.TaskTitle TaskName , rt.Amount , rt.AvailableCount , case when o.OrderStatus is null then -1 else o.OrderStatus end [Status], rt.BeginTime , rt.EndTime , rt.PaymentMethod , o.AuditStatus , rt.TaskCycle , o.CreateTime ReceivedTime, b.logo , o.Id myReceivedTaskId, o.AuditTime , o.Id orderId from dbo.RenRenTask rt ( nolock ) left join dbo.[Order] o ( nolock ) on rt.Id = o.TaskId and o.ClienterId = ${userId} join dbo.Business b (nolock) on rt.BusinessId = b.Id where o.ClienterId is not null and o.OrderStatus = 0
and o.Id > ${nextId}
order by o.Id asc;

   	select count(1) from dbo.RenRenTask rt ( nolock )
        left join dbo.[Order] o ( nolock ) on rt.Id = o.TaskId and o.ClienterId = ${userId} 
where o.ClienterId is not null and o.OrderStatus = 0 

         and o.Id > ${nextId}

select
top ${itemsCount}
rt.Id TaskId , rt.TaskGeneralInfo , rt.Pusher , rt.TaskTitle TaskName , rt.Amount , rt.AvailableCount , case when o.OrderStatus is null then -1 else o.OrderStatus end [Status], o.AuditStatus , rt.BeginTime , rt.EndTime , rt.PaymentMethod , rt.TaskCycle , o.CreateTime ReceivedTime , o.FinishTime , o.Id myReceivedTaskId, o.Id orderId, (SELECT count(1) FROM dbo.[Order] oo where oo.TaskId = rt.id and oo.AuditStatus = 0) WaitAuditCount from dbo.[Order] o join dbo.RenRenTask rt on rt.Id = o.TaskId and o.ClienterId = ${userId} join dbo.Business b (nolock) on rt.BusinessId = b.Id where o.OrderStatus = 1
and o.Id > ${nextId}
order by o.Id asc;

	select count(1) from  dbo.[Order] o
             join dbo.RenRenTask rt on rt.Id = o.TaskId and o.ClienterId =  ${userId} where  o.OrderStatus = 1 

      and o.Id > ${nextId}
