mapper.select:
select
top #{itemsCount}
rt.Id TaskId , rt.TaskGeneralInfo , rt.Pusher , rt.TaskTitle TaskName , rt.Amount , rt.AvailableCount , rt.[Status] , rt.BeginTime , rt.EndTime , rt.PaymentMethod from dbo.RenRenTask rt ( nolock ) left join dbo.[Order] o(nolock) on rt.Id = o.TaskId and o.ClienterId = #{userId} where o.ClienterId is null and o.OrderStatus = 0
and rt.Id > #{nextId}
order by rt.Id desc;

   	select count(1) from dbo.RenRenTask rt ( nolock )
left join  dbo.[Order] o(nolock) on rt.Id  = o.TaskId and o.ClienterId =  #{userId}
where o.ClienterId is null and o.OrderStatus = 0
