mapper.select:
	SELECT TOP 1 tcr.CityName AS Name,tcr.CityCode AS Code,ppc.Jibie,ppc.ParentCode FROM dbo.TaskCityRelation AS tcr  (NOLOCK)
JOIN dbo.PublicProvinceCity AS ppc (NOLOCK) ON tcr.CityCode=ppc.Code
WHERE taskid=#{taskId,jdbcType=BIGINT}

update.foreach:
	insert into TaskCityRelation (TaskId, CityCode, CityName)     
	values (#{item.taskId,jdbcType=BIGINT}, #{item.cityCode,jdbcType=INTEGER}, 
      #{item.cityName,jdbcType=NVARCHAR});
