mapper.insert:
select @@IDENTITY as id
INSERT INTO dbo.TaskDatum ( CtId , ClienterId , TaskId, StrategyId, SubCommisson
) VALUES ( ${ctId} , ${clienterId} , ${taskId}, ${strategyId}, ${subCommisson} )

mapper.select:
SELECT a.status, count(*) as totalNum FROM TaskDatum a ( NOLOCK ) JOIN RenRenTask b ( NOLOCK ) ON a.TaskId = b.id where a.ClienterId = ${userId}
AND a.TaskId = ${taskId}
group by a.Status

SELECT
top ${itemsCount}
b.id AS taskId , b.TaskTitle as taskName, b.amount , b.TaskType , b.Status AS taskStatus , b.auditCycle, a.id AS taskDatumId , a.Status AS auditStatus , a.CreateDate , a.AuditTime, a.CtId, a.refuReason FROM TaskDatum a (nolock) JOIN RenRenTask b (nolock) ON a.TaskId = b.id where a.ClienterId = ${userId} and a.status=${auditStatus}
AND a.TaskId = ${taskId}
and ${nextId}> a.id
order by a.id desc;

SELECT
top ${itemsCount}
a.id AS taskId , a.TaskTitle AS taskName , a.amount , a.TaskType , a.Status AS taskStatus , b.Logo, k.id AS ctid, COUNT(c.Id) AS taskDatumCount FROM RenRenTask a ( NOLOCK ) JOIN dbo.Business b ( NOLOCK ) ON a.BusinessId = b.id JOIN TaskDatum c ( NOLOCK ) ON c.TaskId = a.id JOIN ClienterTask k ( NOLOCK ) ON a.Id=k.TaskId AND k.ClienterId=${userId} WHERE c.ClienterId = ${userId} AND c.Status = ${auditStatus}
and ${nextId}> a.id
GROUP BY a.id , a.TaskTitle , a.TaskType , a.Status , a.Amount , b.Logo, k.id order by a.id desc;

SELECT g1.taskid ,MIN(g1.id) AS groupid INTO #TB FROM TemplateGroup g1 WITH ( NOLOCK ) join ClienterTask c2 WITH ( NOLOCK ) on g1.taskid=c2.taskid WHERE c2.ClienterId = ${userId}
AND c2.TaskId = ${taskId}
GROUP BY g1.taskid
SELECT b.id AS taskDatumId , g.groupType , k.controlName , k.controlValue, m.orderNum FROM ClienterTask a WITH ( NOLOCK ) JOIN TaskDatum b WITH ( NOLOCK ) ON b.TaskId = a.TaskId JOIN #TB ON b.TaskId = #TB.taskid JOIN TemplateGroup g WITH ( NOLOCK ) ON #TB.groupid = g.id JOIN TemplateDetail m WITH ( NOLOCK ) ON m.templategroupid = g.id JOIN TaskDatumChild k WITH ( NOLOCK ) ON m.NAME = k.controlname AND k.taskdatumid = b.id WHERE a.ClienterId = ${userId}
AND a.TaskId = ${taskId}
DROP TABLE #TB

SELECT ${taskDatumId} AS TaskDatumId , ${taskId} as TaskId , a.id AS groupId , a.GroupType , a.Title FROM TemplateGroup a WITH ( NOLOCK )
JOIN TaskDatum b WITH ( NOLOCK ) ON a.TaskId = b.TaskId
WHERE a.TaskId=${taskId}
and b.id=${taskDatumId}
